mapper.update:
		UPDATE MYMEMBER
		SET MEM_NAME = #{memName}, MEM_TEL = #{memTel}, MEM_ADDR = #{memAddr}, ATCH_FILE_ID = #{atchFileId}
		WHERE MEM_ID = #{memId}

mapper.select:
		SELECT MEM_ID, MEM_NAME, MEM_TEL, MEM_ADDR, REG_DT
		FROM MYMEMBER
		WHERE MEM_ID = #{memId}

		SELECT MEM_ID MEMID, MEM_NAME MEMNAME, MEM_TEL MEMTEL, MEM_ADDR MEMADDR,
		REG_DT, ATCH_FILE_ID AS atchFileId
		FROM MYMEMBER
		
			
				AND MEM_ID = #{memId}
			
			
				AND MEM_NAME =#{memName}
			
			
				AND MEM_TEL = #{memTel}
			
			
				AND MEM_ADDR LIKE '%' || #{memAddr} || '%'
